from flask import Flask,render_templateimport randomimport domain as domapp = Flask(__name__)@app.route('/')def index():    liczby=[random.randint(1,100) for e in range(10)]    zmienna='wartość zmiennej'    owoc=dom.Owoc('Pomarańcza','Niebieski')    return render_template('index.html',owoc=owoc,var1=zmienna,var2="cośtam",lista=liczby)@app.route('/todos')def todos():    return render_template('todos.html')@app.route('/info')def info():    numer_wersji="1.0.0.0 Alpha"    return render_template('info.html',ver=numer_wersji)if __name__ == '__main__':    app.run(debug=True,port=80)#Stwórz klasę Author która będzie zawierala pola imię, nazwisko, email.#Zadbaj o to by przy tworzeniu obiektu jego pola byly od razu uzupelniane.#Nie przez konstruktor sparametryzowany tylko bezparametrowy#Stwórz obiekt tej klasy w kontrolerze widoku "info" i przekaż ten obiekt do widoku.#Na poziomie widoku wyswietl te dane w formie tabelarycznej#1. Dodaj modul domain.py#2. Dodaj do niego klasę Author z odpowiednim konstruktorem zapelniajacym pola#3. Zaimportuj te klasę w app.py#4. W kontrolerze widoku info stwórz obiekt klasy Author i przekaż go do widoku#5. Na poziomie widoku wyświetl dane z obiektu w tabelce {{ author.first_name }}